import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function NotaList() {
  const [notas, setNotas] = useState([]);

  const loadNotas = async () => {
    const result = await axios.get('http://localhost:8080/api/notas');
    setNotas(result.data);
  };

  useEffect(() => {
    loadNotas();
  }, []);

  const deleteNota = async (id) => {
    await axios.delete(`http://localhost:8080/api/notas/${id}`);
    loadNotas(); // Refresca la lista después de eliminar
  };

  return (
    <div className="container">
      <div className="py-4">
        <h1>Gestión de Notas</h1>
        <Link className="btn btn-primary" to="/add">Agregar Nota</Link>
        <table className="table border shadow">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Actividades</th>
              <th scope="col">Primer Parcial</th>
              <th scope="col">Segundo Parcial</th>
              <th scope="col">Examen Final</th>
              <th scope="col">Total</th> {/* Añadir la columna Total */}
              <th scope="col">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {notas.map((nota, index) => (
              <tr key={nota.id}>
                <th scope="row">{index + 1}</th>
                <td>{nota.actividades}</td>
                <td>{nota.primerParcial}</td>
                <td>{nota.segundoParcial}</td>
                <td>{nota.examenFinal}</td>
                <td>{nota.total}</td> {/* Mostrar el valor de Total */}
                <td>
                  <Link className="btn btn-warning mr-2" to={`/edit/${nota.id}`}>Editar</Link>
                  <button className="btn btn-danger" onClick={() => deleteNota(nota.id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
